import * as React from "react"
import {
  ChakraProvider,
  CSSReset,
  Box,
  Grid,
  GridItem,
  useColorModeValue
} from "@chakra-ui/core"
import theme from "@chakra-ui/theme"
import { Router as ReachRouter, RouteComponentProps } from "@reach/router"
import { ColorModeSwitcher } from "./ColorModeSwitcher"

import { UminPageWrapper } from './pages/UminPageWrapper'
import { Login } from './pages/Login';
import { Register } from './pages/Register';
import { TestPage } from './pages/TestPage'
import { Header } from './components/Header';
import { Menu } from './components/Menu';
import { Content } from './components/Content';

export const App = () => {
  const globalBgColor = useColorModeValue("white", "")
  const globalColor = useColorModeValue("rgba(138, 138, 138, 1)", "white")

  return (
    <Box>
    <ChakraProvider theme={theme}>
      <CSSReset />

      <Box position="absolute" top="100" right="100">
        <ColorModeSwitcher />
      </Box>

      <ReachRouter>
        <Login path="login" />
        <Register path="register" /> 
        <UminPageWrapper path="/umi">
          <TestPage path="/test" />
        </UminPageWrapper>
      </ReachRouter>

      </ChakraProvider>

    </Box>
  )
}
