import React from 'react'
import { Box, Text, Heading, InputGroup, InputLeftElement, useColorModeValue } from '@chakra-ui/core';
import { AiOutlineMail } from 'react-icons/ai';

interface IProps {
  type: 'register' | 'login'
}

export const AccountCard: React.FC<IProps> = ({ type }) => {
  const textColor = useColorModeValue('#1A202C', 'white')
  const cardBgColor = useColorModeValue('white', '#1A202C')

  return (
    <Box display="flex" flexDir="column" justifyContent="center" alignItems="center" shadow="md" borderWidth="1px" borderRadius="md" padding={10}>
      <Heading as="h2" size="xl">{type === "login" ? "Sign In" : "Sign Up"}</Heading>
      <Text fontSize="lg">{type === "login" ? "Do not have an account?" : "Already have an accuont ?"}</Text>
      <InputGroup>
        <InputLeftElement
        pointerEvents="none"
        children={<AiOutlineMail color="gray.300" />}
      />
      </InputGroup>
    </Box>
  )
}
