import React, {useState} from 'react'
import { Box, Text, Heading, InputGroup, InputLeftElement, Input, Button, useColorModeValue, InputRightElement } from '@chakra-ui/core';
import { AiOutlineMail, AiOutlineEye, AiOutlineEyeInvisible } from 'react-icons/ai';

interface IProps {
  type: 'register' | 'login'
}

export const AccountCard: React.FC<IProps> = ({ type }) => {
  const textColor = useColorModeValue('#1A202C', 'white')
  const cardBgColor = useColorModeValue('white', '#1A202C')
  const [emailError, setEmailError] = useState(false)
  const [passwordError, setPasswordError] = useState(false)
  const [showPassword, setShowPassword] = useState(false)

  return (
    <Box
      display="flex"
      flexDir="column"
      justifyContent="space-between"
      alignItems="center"
      shadow="md"
      borderWidth="1px"
      borderRadius="md"
      padding={10}
      minW={300}
      minH={300}
      maxW={550}
      maxH={600}
    >
      <Heading as="h2" size="xl">{type === "login" ? "Sign In" : "Sign Up"}</Heading>
      <Text fontSize="lg">
        {type === "login" ? "Do not have an account?" : "Already have an accuont ?"}
      </Text>
      <InputGroup fontSize="md">
        <InputLeftElement
          pointerEvents="none"
          children={<AiOutlineMail color="gray.300" />}
        />
        <Input
          type="text"
          focusBorderColor="#1A202C"
          isInvalid={emailError === true ? true: false}
          errorBorderColor={emailError === true ? "red.300" : ""}
          placeholder="Email"
        />
      </InputGroup>
      <InputGroup fontSize="md">
        <InputLeftElement
          pointerEvents="none"
          children={showPassword ? <AiOutlineEye color="gray.300" /> : <AiOutlineEyeInvisible color="gray.300" />}
        />
        <Input
          type="password"
          focusBorderColor="#1A202C"
          isInvalid={passwordError === true ? true: false}
          errorBorderColor={passwordError === true ? "red.300" : ""}
          placeholder="Password"
        />
        <InputRightElement>
          <Button h="1.75rem" w="2rem" size="sm" onClick={() => setShowPassword(!showPassword)}>
            {showPassword ? "Hide" : "Show"}
          </Button>
        </InputRightElement>
      </InputGroup>
    </Box>
  )
}
