import React, {useState} from 'react'
import { Box, Text, Heading, InputGroup, InputLeftAddon, Input, Button, useColorModeValue, InputRightElement } from '@chakra-ui/core';
import {FaRegEnvelope, FaRegEye, FaRegEyeSlash} from 'react-icons/fa'

interface IProps {
  type: 'register' | 'login'
}

export const AccountCard: React.FC<IProps> = ({ type }) => {
  const textColor = useColorModeValue('#1A202C', 'white')
  const cardBgColor = useColorModeValue('white', '#1A202C')
  const [emailError, setEmailError] = useState(false)
  const [passwordError, setPasswordError] = useState(false)
  const [showPassword, setShowPassword] = useState(false)

  return (
    <Box
      display="flex"
      flexDir="column"
      justifyContent="space-between"
      alignItems="center"
      shadow="md"
      borderWidth="1px"
      borderRadius="md"
      padding={10}
      minW={300}
      minH={400}
      maxW={560}
      maxH={600}
      color={textColor}
      backgroundColor={cardBgColor}
    >
      <Heading as="h2" size="xl">
        {type === "login" ? "Sign In" : "Sign Up"}
      </Heading>
      <Text fontSize="lg">
        {type === "login" ? "Do not have an account?" : "Already have an accuont ?"}
      </Text>
      <InputGroup fontSize="lg">
        <InputLeftAddon
          pointerEvents="none"
          children={<FaRegEnvelope color="gray.300" />}
        />
        <Input
          type="text"
          pl="20px"
          focusBorderColor="#1A202C"
          isInvalid={emailError === true ? true: false}
          errorBorderColor={emailError === true ? "red.300" : ""}
          placeholder="Email"
        />
      </InputGroup>
      <InputGroup fontSize="lg">
        <InputLeftAddon
          pointerEvents="none"
          children={showPassword ? <FaRegEye color="gray.300" /> : <FaRegEyeSlash color="gray.300" />}
        />
        <Input
          type="password"
          focusBorderColor="#1A202C"
          pl="20px"
          isInvalid={passwordError === true ? true: false}
          errorBorderColor={passwordError === true ? "red.300" : ""}
          placeholder="Password"
        />
        <InputRightElement>
          <Button
            h="2rem"
            w="3rem"
            size="md"
            mr={2}
            outline="none"
            onClick={() => setShowPassword(!showPassword)}
          >
            <Text color="gray.500">{showPassword ? "Hide" : "Show"}</Text>
          </Button>
        </InputRightElement>
      </InputGroup>
      <Button outline="none" color={textColor}>
        Sign In
      </Button>
    </Box>
  )
}
