import React, {useState} from 'react'
import { Box, Text, Heading, InputGroup, InputLeftElement, Input, useColorModeValue } from '@chakra-ui/core';
import { AiOutlineMail } from 'react-icons/ai';

interface IProps {
  type: 'register' | 'login'
}

export const AccountCard: React.FC<IProps> = ({ type }) => {
  const textColor = useColorModeValue('#1A202C', 'white')
  const cardBgColor = useColorModeValue('white', '#1A202C')
  const [emailError, setEmailError] = useState(false)
  const [asswordError, setPasswordError] = useState(false)

  return (
    <Box
      display="flex"
      flexDir="column"
      justifyContent="space-between"
      alignItems="center"
      shadow="md"
      borderWidth="1px"
      borderRadius="md"
      padding={10}
      minW={300}
      minH={300}
      maxW={550}
      maxH={600}
    >
      <Heading as="h2" size="xl">{type === "login" ? "Sign In" : "Sign Up"}</Heading>
      <Text fontSize="lg">
        {type === "login" ? "Do not have an account?" : "Already have an accuont ?"}
      </Text>
      <InputGroup>
        <InputLeftElement
        pointerEvents="none"
        children={<AiOutlineMail color="gray.300" />}
      />
        <Input
          type="phone"
          focusBorderColor="#1A202C"
          isInvalid={emailError === true ? true: false}
          errorBorderColor={emailError === true ? "red.300" : ""}
          placeholder="Email"
        />
      </InputGroup>
    </Box>
  )
}
