import * as React from "react"
import {
  ChakraProvider,
  CSSReset,
  Box,
  Grid,
  GridItem,
  useColorModeValue
} from "@chakra-ui/core"
import theme from "@chakra-ui/theme"
import { Router as ReachRouter, RouteComponentProps } from "@reach/router"

import { Login } from './pages/Login';
import { Register } from './pages/Register';
import { Header } from './components/Header';
import { Menu } from './components/Menu';

interface IProps extends RouteComponentProps {
  children: JSX.Element[] | JSX.Element
}

export const App: React.FC<IProps> = ({children}) => {
  const globalBgColor = useColorModeValue("white", "")
  const globalColor = useColorModeValue("rgba(138, 138, 138, 1)", "white")

  return (
    <ChakraProvider theme={theme}>
      <CSSReset />
      <Box textAlign="center" fontSize="md" bgColor={globalBgColor} color={globalColor}>
        <Header />

        <ReachRouter>
          <Login path="login" />
          <Register path="register" />
        </ReachRouter>

        {/* COMMENT: main body */}
        {/* <Grid
          templateColumns="repeat(4, 1fr)"
          gap={4}
          minW="960px"
          maxW="1120px"
          w="100%"
          mx="auto"
          minH="800px"
        >
          <GridItem colSpan={1}>
            <Menu />
          </GridItem>
          <GridItem colSpan={3}>
            {children}
          </GridItem>
        </Grid> */}
      </Box>
    </ChakraProvider>
  )
}
